import React, { Component } from 'react';
import Navbar from "./navbar"
import { connect } from "react-redux";
// import { bindActionCreators } from "redux";
// import { addBoard } from "../actions/action"

class Home extends Component {
    render() {
        return (
            <div>
                <Navbar />
                <div className="app">
                    {(this.props.boards).length === 0 &&
                        <h4 className="text-white font-weight-lighter text-center">No Trello Boards Found!</h4>
                    } 
                    {(this.props.boards).length !== 0 &&
                        (this.props.boards.map((elem, index) => {
                            return <div key={index} className="card" >
                                <div className="card-body" style={{"width": "20rem"}}>
                                    <h5 className="card-title">{elem.boardName}</h5>
                                </div>
                            </div>
                        }))
                    }
                </div> 

            </div>
        )
    }

}

const mapStateToProps = (state) => {
    console.log(state)
    return {
        boards: state.appReducer.boards
    }

}
// const mapDispatchToProps =(dispatch)=> {
//     return bindActionCreators({
//         addBoard
//     },dispatch)

// }

export default connect(mapStateToProps)(Home);
