export function addBoard(data) { 
    return {
        type: "addBoard",
        payload: data
    }
 }

 export function deleteItem(index){
    return {
        type: "deleteItem",
        payload: index
    }
}

export function editField(data, index){
    return {
        type: "editField",
        payload: {
            data: data,
            index: index
        }
    }
}

export function editIndex(id){
    return{
        type: "editIndex",
        payload: id
    }
}
