import React, { Component } from 'react';
import { connect } from 'react-redux';
import { Link } from "react-router-dom";
import { addList, addCard } from "../actions/action";
import { bindActionCreators } from "redux";
import { DragDropContext, Draggable, Droppable } from "react-beautiful-dnd"



class MyBoard extends Component {

    state = {
        listName: "",
        cardName: ""
    }

    editData = (field, index) => {
        this.props.editField(field, index)
    }

    onDragEnd = () => {

    }

    render() {
        console.log(this.props)
        let { index: BoardNum } = this.props.location.state
        const { boards } = this.props
        const elem = boards[BoardNum]
        return (
            <div>
                <nav className="navbar navBar">
                    <div className="d-flex col">
                        <div className="row col-md-5">
                            <i className="fa fa-home rounded mr-2" aria-hidden="true"></i>
                            <input className="searchInp rounded" type="search" placeholder="Search..." />
                        </div>
                        <div className="col-md-2 text-center">
                            <Link to="/" style={{ textDecoration: 'none', color: 'white' }} ><h4 className="trello"><i className="fa fa-trello mr-1" aria-hidden="true"></i>Trello</h4></Link>
                        </div>
                        <div className="col">
                            <div className="float-right font-weight-bolder text-white">{elem.boardName}</div>
                        </div>
                    </div>
                </nav>
                <DragDropContext onDragEnd={this.onDragEnd}>
                    <div className="d-flex">{elem.listArray.map((elem, index) => {
                        return <Droppable droppableId={`list${index}`}>
                            {(provider) => {
                                <div {...provider.droppableProps} ref={provider.innerRef} key={index} className="bg-dark text-white p-2">
                                    <div>{elem.listName}</div>
                                    <input style={{ background: "transparent" }} placeholder="Add Card..." type="text" onChange={(e) => this.setState({ cardName: e.target.value })} />
                                    <button className="bg-light" onClick={() => this.props.addCard(this.state.cardName, BoardNum, index)}>Add <i className="fa fa-plus" aria-hidden="true"></i></button>
                                    {elem.cards.map((elm, cardindex) => {
                                        return <Draggable index={cardindex} draggableId={`list${index}card${cardindex}`}>
                                            {(provider) => {
                                                <div {...provider.draggableProps} ref={provider.innerRef} {...provider.dragHandleProps} key={cardindex} className="bg-light shadow rounded text-dark font-weight-bolder">
                                                    {elm.cardName}
                                                </div>
                                            }
                                            }
                                        </Draggable>
                                    })}
                                </div>
                            }
                            }
                        </Droppable>
                    })}
                    </div>
                </DragDropContext>
                <input style={{ background: "transparent" }} placeholder="Add List..." type="text" onChange={(e) => this.setState({ listName: e.target.value })} />
                <button className="bg-light" onClick={() => this.props.addList(this.state.listName, BoardNum)}>Add <i className="fa fa-plus" aria-hidden="true"></i></button>
            </div>
        );

    }
}

const mapStateToProps = (state) => {
    console.log("myboard state", state)
    return {
        boards: state.appReducer.boards
    };
}
const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        addList, addCard
    }, dispatch)

}
export default connect(
    mapStateToProps, mapDispatchToProps
)(MyBoard);
