export function addBoard(data) {
    return {
        type: "addBoard",
        payload: data
    }
}

export function deleteItem(index) {
    return {
        type: "deleteItem",
        payload: index
    }
}

export function editField(data, index) {
    return {
        type: "editField",
        payload: {
            data: data,
            index: index
        }
    }
}

export function editIndex(name) {
    return {
        type: "editIndex",
        payload: name
    }
}

export function boardClicked(Bindex , ind) {
    return {
        type: "boardClicked",
        payload: {
            Bindex: Bindex,
            ind: ind
        }
    }
}

export function setClick() {
    return {
        type: "setClick"
    }
}

export function addList(data, index) {
    return {
        type: "addList",
        payload: {
            data: data,
            index: index
        }
    }
}

export function addCard(data, Bindex, Lindex) {
    return {
        type: "addCard",
        payload: {
            data: data,
            Bindex: Bindex,
            Lindex: Lindex,
        }
    }
}

export function sorted(droppableIdStart,droppableIdEnd,droppableIndexStart,droppableIndexEnd,draggableId, BoardNum) {
    return {
        type: "sorted",
        payload: {
            droppableIdStart,
            droppableIdEnd,
            droppableIndexStart,
            droppableIndexEnd,
            draggableId,
            BoardNum
        }
    }
}
