export function addBoard(data) { 
    return {
        type: "addBoard",
        payload: data
    }
 }

 export function deleteItem(index){
    return {
        type: "deleteItem",
        payload: index
    }
}

export function editField(data, index){
    return {
        type: "editField",
        payload: {
            data: data,
            index: index
        }
    }
}

export function editIndex(name){
    return{
        type: "editIndex",
        payload: name
    }
}

export function boardClicked(ind){
    return{
        type: "boardClicked",
        payload: ind
    }
}

export function setClick(){
    return {
        type: "setClick"
    }
}

export function addList(){
    return {
        type: "addList"
    }
}
