import React, { Component } from 'react';
import { connect } from 'react-redux';
import { addList, addCard, sorted, editField } from "../actions/action";
import { bindActionCreators } from "redux";
import { Draggable, Droppable } from "react-beautiful-dnd"

class ListComp extends Component {
    render() {
        return (
            <div>
                <Droppable droppableId={`list${index}`}>
                    {(provider) => (
                        <div {...provider.droppableProps} ref={provider.innerRef} key={index} className="bg-white p-2 rounded m-1">
                            <div className="font-weight-bolder">{elem.listName}</div>
                            {/* <input style={{ background: "transparent" }} placeholder="Add Card..." type="text" onChange={(e) => this.setState({ cardName: e.target.value })} />
                                        <button className="bg-light" onClick={() => this.props.addCard(this.state.cardName, BoardNum, index)}>Add <i className="fa fa-plus" aria-hidden="true"></i></button> */}
                            {elem.cards.map((elm, cardindex) => {
                                return <Draggable index={cardindex} draggableId={`list${index}card${cardindex}`}>
                                    {(provider) => (
                                        <div {...provider.draggableProps} ref={provider.innerRef} {...provider.dragHandleProps} key={cardindex} className="bg-light p-2 m-2 shadow rounded text-dark">
                                            {elm.cardName}
                                        </div>
                                    )
                                    }
                                </Draggable>
                            })}
                            <div className="text-center rounded addBtn">
                                <i className="fa fa-plus" aria-hidden="true" /> Add
                                        </div>
                        </div>
                    )
                    }
                </Droppable>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    console.log("myboard state", state)
    return {
        boards: state.appReducer.boards
    };
}
const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        addList, addCard, sorted, editField
    }, dispatch)

}
export default connect(
    mapStateToProps, mapDispatchToProps
)(ListComp);
