import React, { Component } from 'react';
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { deleteItem } from "../actions/action"

class Home extends Component {
    render() {
        return (
            <div>
                <div className="app">
                    {(this.props.boards).length === 0 &&
                        <h4 className="text-white font-weight-lighter text-center">No Trello Boards Available!</h4>
                    } 

                    <div className="d-flex justify-content-start">
                        {(this.props.boards).length !== 0 &&
                        (this.props.boards.map((elem, index) => {
                            return <div key={index} className="card m-3 shadow">
                                <div className="card-body">
                                    <h5 className="card-title p-2 bg-light">{elem.boardName}</h5>
                                </div>
                                <div className="ml-auto">
                                        <i className="fa fa-pencil m-2 text-secondary" aria-hidden="true"></i>
                                    <i style={{cursor: 'pointer'}} className="fa fa-trash m-2 text-secondary" onClick={() => { this.props.deleteItem(index)}} aria-hidden="true"></i>
                                    </div>
                            </div>
                        }))
                    }
                    </div>    
                </div> 
            </div>
        )
    }

}

const mapStateToProps = (state) => {
    console.log(state)
    return {
        boards: state.appReducer.boards
    }

}
const mapDispatchToProps =(dispatch)=> {
    return bindActionCreators({
        deleteItem
    },dispatch)

}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
