import React, { Component } from 'react';
import Navbar from "./navbar"
import { connect } from "react-redux";
// import { bindActionCreators } from "redux";
// import { addBoard } from "../actions/action"

class Home extends Component {
    render() {
        return (
            <div>
                <Navbar />
                <div className="app">
                    {(this.props.boards).length === 0 &&
                        <h4 className="text-white font-weight-lighter text-center">No Trello Boards Found!</h4>
                    }
                    {(this.props.boards).length !== 0 &&
                        (this.props.boards.map((elem, index) => {
                            return <div key={index} className="card" style={{ width: "20rem" }}>
                                <div className="card-body">
                                    <h5 className="card-title">Card title</h5>
                                    <h6 className="card-subtitle mb-2 text-muted">Card subtitle</h6>
                                    <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                                </div>
                            </div>
                        }))
                    }
                </div>
            </div>
        )
    }

}

const mapStateToProps = (state) => {
    console.log(state)
    return {
        boards: state.appReducer.boards.board
    }

}
// const mapDispatchToProps =(dispatch)=> {
//     return bindActionCreators({
//         addBoard
//     },dispatch)

// }

export default connect(mapStateToProps)(Home);
