import { combineReducers } from "redux";

let initialState = {
    userName: "",
    boards: [],
    boardName: []
}

function appReducer(state = initialState, action) {
    let stateCopy = JSON.parse(JSON.stringify(state))
    switch (action.type) {
        case "addBoard":
            return addToList(stateCopy, action.payload)
        case "deleteItem":
            let newBoards = stateCopy.boards.filter((el, i) => i !== action.payload)
            stateCopy.boards = newBoards
            return stateCopy;
        case "editField":
            return editField(stateCopy, action.payload.data, action.payload.index)
        case "editIndex":
            let newBoard = stateCopy.boards.indexOf(action.payload)
            stateCopy.boards = newBoard
            return stateCopy;
        case "boardClicked":
            return clicked(stateCopy, action.payload)
        case "setClick":
            return setClick(stateCopy)
        case "addList":
            return addList(stateCopy)
        default:
            return stateCopy;
    }
}

const rootReducers = combineReducers({
    appReducer
})

export default rootReducers;

let addToList = (state, data) => {
    let newBoardList = state.boards.slice()
    newBoardList.push(data)
    state.boards = newBoardList;
    return state;
}

let editField = (state, data, index) => {
    state.boards.map((element, i) => {
        if (i === index) {
            element.boardName = data
            element.isSet = !element.isSet
        }
        return true
    });
    return state
}

let clicked = (state, index) => {
    state.boards.map((elem, i) => {
        if (i === index) {
            elem.boardClicked = !elem.boardClicked
        }
        return true
    })
    return state
}

let setClick = (state) => {
    state.boards.map((elem, index) => {
        if (elem.boardClicked) {
            elem.boardClicked = false
        }
        return true
    })
    return state
}

let addList = (state) => {
    let listArray = []
    state.boards.push(listArray)
    return state
}
