import React, { Component } from 'react';
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { bindActionCreators } from "redux";
import { deleteItem, addBoard, editField , editIndex} from "../actions/action"

class Home extends Component {

    state = {
        boardName: ""
    }

    addData = () => {
        let boardData = {
            board: {
                "boardName": this.state.boardName,
                "isSet": false
            }
        }
        this.props.addBoard(boardData.board)
    }

    editData = (field, index) => {
        this.props.editField(field, index)
    }

    componentDidUpdate(prevProps){
        if(prevProps!== this.props){
            this.props.editIndex(this.props.boards)
        }
    }

    render() {
        console.log(this.state)
        return (
            <div>
                <nav className="navbar navBar">
                    <div className="d-flex col">
                        <div className="row col-md-5">
                            <i className="fa fa-home rounded mr-2" aria-hidden="true"></i>
                            <input className="searchInp rounded" type="search" placeholder="Search..." />
                        </div>
                        <div className="col-md-2 text-center">
                            <Link to="/" style={{ textDecoration: 'none', color: 'white' }} ><h4 className="trello"><i className="fa fa-trello mr-1" aria-hidden="true"></i>Trello</h4></Link>
                        </div>
                        <button data-toggle="modal" data-target="#exampleModal" className="button rounded ml-auto"><i className="fa fa-plus" aria-hidden="true"></i> Create New Board</button>
                    </div>
                    <Link to="/myboard"><div className="bg-dark p-2">go</div></Link>
                </nav>

                <div className="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="exampleModalLabel">New Board</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                            <div className="modal-body">
                                <input value={this.state.boardName} onChange={(e) => this.setState({ boardName: e.target.value })} className="w-75 rounded searchInp" type="text" placeholder="Enter Title" />
                            </div>
                            <div className="d-flex justify-content-end">
                                <button type="button" className="btn btn-secondary m-2" data-dismiss="modal">Cancel</button>
                                <div onClick={() => this.addData()} data-dismiss="modal" className="btn btn-success m-2">Save Board</div>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="app">
                    {(this.props.boards).length === 0 &&
                        <h4 className="text-white font-weight-lighter text-center">No Trello Boards Available!</h4>
                    }

                    <div className="d-flex justify-content-start">
                        {(this.props.boards).length !== 0 &&
                            (this.props.boards.map((elem, index) => {
                                return <div key={index} className="card m-3 shadow">
                                    <div className="card-body">
                                        <textarea onChange={(e) => this.setState({ boardName: e.target.value })} rows="1" className="card-title p-2 bg-light rounded">{elem.boardName}</textarea>
                                    </div>
                                    <button onClick={() => this.editData(this.state.boardName, index)}>{elem.isSet ? "Save" : "Edit Title"}</button>
                                    <div className="ml-auto">
                                        <i style={{ cursor: 'pointer' }} data-toggle="modal" data-target="#exampleModal" className="fa fa-pencil m-2 text-secondary" aria-hidden="true"></i>
                                        <i style={{ cursor: 'pointer' }} className="fa fa-trash m-2 text-secondary" onClick={() => { this.props.deleteItem(index) }} aria-hidden="true"></i>
                                    </div>
                                </div>
                            }))
                        }
                    </div>
                </div>
            </div>
        )
    }

}

const mapStateToProps = (state) => {
    console.log(state)
    return {
        boards: state.appReducer.boards
    }

}
const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        deleteItem, addBoard, editField, editIndex
    }, dispatch)

}

export default connect(mapStateToProps, mapDispatchToProps)(Home);
