import React, { Component } from 'react';
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { addBoard } from "../actions/action";
import { Link } from "react-router-dom";


class Navbar extends Component {

    state = {
        boardName: "",
    }

    addData = () => {
        let boardData = {
            board: {
                "boardName": this.state.boardName
            }
        }
        this.props.addBoard(boardData.board)
    }

    render() {
        return (
            <div>
                <nav className="navbar navBar">
                    <div className="d-flex col">
                        <div className="row col-md-5">
                            <Link style={{ textDecoration: 'none', color: 'white' }} to="/"><i className="fa fa-home rounded mr-2" aria-hidden="true"></i></Link>
                            <input className="searchInp rounded" type="search" placeholder="Search..." />
                        </div>
                        <div className="col-md-2 text-center">
                            <h4 className="trello"><i className="fa fa-trello mr-1" aria-hidden="true"></i>Trello</h4>
                        </div>
                        <button data-toggle="modal" data-target="#exampleModal" className="button rounded ml-auto"><i className="fa fa-plus" aria-hidden="true"></i> Create New Board</button>
                    </div>
                </nav>

                <div className="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                    <div className="modal-dialog" role="document">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title" id="exampleModalLabel">New Board</h5>
                                <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                    <span aria-hidden="true">&times;</span>
                                </button>
                            </div>
                                <div className="modal-body">
                                    <input onChange={(e) => this.setState({ boardName: e.target.value })} className="w-75 rounded searchInp" type="text" placeholder="Enter Title" />
                                </div>
                                <div className="d-flex justify-content-end">
                                    <button type="button" className="btn btn-secondary m-2" data-dismiss="modal">Cancel</button>
                                        <button onClick={()=>this.addData()} data-dismiss="modal" className="btn btn-success m-2">Save Board</button>
                                </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}
const mapStateToProps = (state) => {
    console.log(state)
    return {
        boards: state.appReducer.boards
    }

}
const mapDispatchToProps = (dispatch) => {
    return bindActionCreators({
        addBoard
    }, dispatch)

}

export default connect(mapStateToProps, mapDispatchToProps)(Navbar);
